/* Darwin UI Styles */

@import "tailwindcss";

@layer base {
  :root {
    --background: 0 0% 4%;
    --foreground: 0 0% 95%;
  }
}

@layer utilities {
  .animate-pulse {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes pulse {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.5;
    }
  }
}
